export function zipCode(value) {
  return /^\w{1,9}$/.test(value) ? null : `Enter valid zip code`;
}

export function ascii(value, label) {
  return /^[\x00-\x7F]+$/.test(value) ? null : `${label} must contain only ASCII characters`;
}

export function phoneNumber(value) {
  return /^[\d#\-\(\)\+\*) ]+$/.test(value) ? null : `Please enter a valid phone number`;
}

export function cvv(value) {
  return value.length != 3 ? `Please enter a valid cvv number` : null;
}
